/* Local stub loader for pdf.min.js
   This small file provides a same-origin URL for the page to load.
   It dynamically injects the official pdf.min.js from the CDN.
   For best performance and offline resilience, replace this file with the real
   minified build of PDF.js (copy pdf.min.js from the official distribution).
*/
(function(){
  try {
    if (window.pdfjsLib) {
      console.log('pdf.min.js: pdfjsLib already present.');
      return;
    }
  var s = document.createElement('script');
  // Version-pinned CDN as a reliable fallback when the full build is not
  // checked into the repo. Change the version here if you update PDF.js.
  s.src = 'https://unpkg.com/pdfjs-dist@2.16.105/build/pdf.min.js';
    s.crossOrigin = 'anonymous';
    s.onload = function(){ console.log('pdf.min.js stub loaded CDN build.'); };
    s.onerror = function(e){ console.error('pdf.min.js stub failed to load CDN build.', e); };
    document.head.appendChild(s);
  } catch (e) { console.error('pdf.min.js stub error', e); }
})();
