/* Local worker stub for pdf.worker.min.js
   This file is executed inside the Worker context. To avoid shipping the entire
   minified worker here, we import the official worker build from the CDN.
   For full self-hosting, replace this file with the real `pdf.worker.min.js`.
*/
try {
  // importScripts is available inside Worker context. This will pull the
  // official worker code into this worker. The worker is still initiated from
  // a same-origin URL (this file), which helps some browser policies.
  // Use a version-pinned worker build from the official distribution.
  // Keeping this file as a small same-origin stub lets us set
  // pdfjsLib.GlobalWorkerOptions.workerSrc = '/js/vendor/pdf.worker.min.js'
  // while still pulling the official worker code from the CDN if the full
  // worker isn't checked into the repo.
  importScripts('https://unpkg.com/pdfjs-dist@2.16.105/build/pdf.worker.min.js');
} catch (e) {
  // If this file is accidentally loaded in the main thread, fail gracefully
  // and log an informative message.
  if (typeof window !== 'undefined' && window.console) {
    console.error('pdf.worker.min.js stub: importScripts failed (not in Worker?),', e);
  }
}
